mapper.update:

update board 
   set title=#{title },
       contents=#{contents }
 where no=#{no }
   and user_no=#{userNo }

mapper.delete:


delete
  from board 
 where no = #{no }
   and user_no = #{userNo }

mapper.select:

select	no,
		title,
		contents
   from board
  where no = #{no }
    and user_no = #{userNo }

mapper.update:

update board
   set hit = hit + 1
 where no=#{no }


update board
   set o_no = o_no + 1
 where g_no = #{groupNo }
   and o_no >= #{orderNo }
